DROP TABLE IF EXISTS public.roadmap;DROP TABLE IF EXISTS public.barter;DROP TABLE IF EXISTS public.users;DROP TABLE IF EXISTS public.jobs;DROP TABLE IF EXISTS public.courses;CREATE TABLE public.users (    id           BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,    name         TEXT NOT NULL,    email        TEXT UNIQUE NOT NULL,    password     TEXT NOT NULL,    skills       TEXT[] DEFAULT '{}',               location     TEXT DEFAULT '',    bio          TEXT DEFAULT '',    profile_img  TEXT DEFAULT '',    created_at   TIMESTAMP WITH TIME ZONE DEFAULT timezone('utc'::text, now()) NOT NULL);CREATE TABLE public.jobs (    id              BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,    title           TEXT NOT NULL,    icon            TEXT DEFAULT 'heart',    required_skills JSONB DEFAULT '[]'::jsonb,    created_at      TIMESTAMP WITH TIME ZONE DEFAULT timezone('utc'::text, now()) NOT NULL);CREATE TABLE public.courses (    id         BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,    name       TEXT NOT NULL,    provider   TEXT NOT NULL,    duration   TEXT NOT NULL,    link       TEXT,    created_at TIMESTAMP WITH TIME ZONE DEFAULT timezone('utc'::text, now()) NOT NULL);CREATE TABLE public.barter (    id            BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,    offer         TEXT NOT NULL,    want          TEXT NOT NULL,    location      TEXT NOT NULL,    teaching_mode TEXT DEFAULT 'In-Person',    user_id       BIGINT REFERENCES public.users(id),    created_at    TIMESTAMP WITH TIME ZONE DEFAULT timezone('utc'::text, now()) NOT NULL);CREATE TABLE public.roadmap (    id              BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,    user_id         BIGINT REFERENCES public.users(id) ON DELETE CASCADE NOT NULL,    skill_name      TEXT NOT NULL,    status          TEXT DEFAULT 'planned' CHECK (status IN ('planned', 'in_progress', 'completed')),    course_name     TEXT DEFAULT '',    course_provider TEXT DEFAULT '',    course_link     TEXT DEFAULT '',    target_date     DATE,    notes           TEXT DEFAULT '',    created_at      TIMESTAMP WITH TIME ZONE DEFAULT timezone('utc'::text, now()) NOT NULL);